#{item}
and now() between info.start_time and info.end_time
select info.activity_type as activityType, rule.id,rule.activity_id,rule.condition_amount,rule.condition_num,rule.benefit_amount,rule.benefit_discount from activity_info info inner join activity_sku sku on sku.activity_id = info.id inner join activity_rule rule on rule.activity_id = info.id where sku.sku_id = #{skuId} and now() between info.start_time and info.end_time order by rule.condition_amount desc, rule.condition_num desc
